import React, { useState } from 'react';
import Modal from 'react-modal';
import { useEffect } from 'react';
import { logCommunication } from '../../utils/api';

const CommunicationModal = ({ isOpen, onRequestClose, companyId, existingCommunications }) => {
    const [communicationType, setCommunicationType] = useState('');
    const [date, setDate] = useState('');
    const [notes, setNotes] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const communicationData = {
            companyId,
            type: communicationType,
            date,
            notes,
        };
        await logCommunication(communicationData);
        onRequestClose(); // Close the modal after logging communication
    };

    useEffect(() => {
        if (existingCommunications) {
            // Reset form if there are existing communications
            setCommunicationType('');
            setDate('');
            setNotes('');
        }
    }, [existingCommunications, isOpen]);

    return (
        <Modal isOpen={isOpen} onRequestClose={onRequestClose} ariaHideApp={false}>
            <h2>Log Communication</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="communicationType">Communication Type:</label>
                    <select
                        id="communicationType"
                        value={communicationType}
                        onChange={(e) => setCommunicationType(e.target.value)}
                        required
                    >
                        <option value="">Select...</option>
                        <option value="LinkedIn Post">LinkedIn Post</option>
                        <option value="LinkedIn Message">LinkedIn Message</option>
                        <option value="Email">Email</option>
                        <option value="Phone Call">Phone Call</option>
                        <option value="Other">Other</option>
                    </select>
                </div>
                <div>
                    <label htmlFor="date">Date:</label>
                    <input
                        type="date"
                        id="date"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="notes">Notes:</label>
                    <textarea
                        id="notes"
                        value={notes}
                        onChange={(e) => setNotes(e.target.value)}
                    />
                </div>
                <button type="submit">Log Communication</button>
                <button type="button" onClick={onRequestClose}>Cancel</button>
            </form>
        </Modal>
    );
};

export default CommunicationModal;
