import React from 'react';
import PropTypes from 'prop-types';

const CompanyCard = ({ company, onClickCompany }) => {
  return (
    <div
      className="company-card bg-gradient-to-r from-white to-blue-50 p-6 rounded-xl shadow-md hover:shadow-lg transition-all cursor-pointer border border-gray-200"
      onClick={() => onClickCompany(company._id)}
    >
      <h3 className="text-xl font-semibold text-blue-700 mb-2">{company.name}</h3>
      <p className="text-gray-600 mb-1">
        <strong>Location:</strong> {company.location}
      </p>
      <p className="text-gray-600 mb-1">
        <strong>Email:</strong> {company.emails.join(', ')}
      </p>
      <p className="text-gray-600 mb-1">
        <strong>Phone:</strong> {company.phoneNumbers.join(', ')}
      </p>
      <p className="text-gray-600 mb-1">
        <strong>LinkedIn:</strong> {company.linkedinProfile}
      </p>
    </div>
  );
};

CompanyCard.propTypes = {
  company: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    location: PropTypes.string.isRequired,
    emails: PropTypes.arrayOf(PropTypes.string).isRequired,
    phoneNumbers: PropTypes.arrayOf(PropTypes.string).isRequired,
    linkedinProfile: PropTypes.string,
  }).isRequired,
  onClickCompany: PropTypes.func.isRequired,
};

export default CompanyCard
