import React from 'react';
import PropTypes from 'prop-types';

const CompanyCard = ({ company, onOpenModal, onClickCompany }) => {
  return (
    <div
      className="companyCard bg-white p-4 rounded shadow hover:shadow-lg transition-shadow cursor-pointer"
      onClick={() => onClickCompany(company._id)}
    >
      <h3 className="text-xl font-semibold mb-2">{company.name}</h3>
      <p className="text-gray-700 mb-1">{company.location}</p>
      <p className="text-gray-700 mb-1">{company.emails.join(', ')}</p>
      <p className="text-gray-700 mb-1">{company.phoneNumbers.join(', ')}</p>
      <p className="text-gray-700 mb-1">{company.linkedinProfile}</p>
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded mt-2"
        onClick={(e) => {
          e.stopPropagation(); // Prevent the card click event from firing
          onOpenModal(company._id);
        }}
      >
        Log Communication
      </button>
    </div>
  );
};

CompanyCard.propTypes = {
  company: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    location: PropTypes.string.isRequired,
    emails: PropTypes.arrayOf(PropTypes.string).isRequired,
    phoneNumbers: PropTypes.arrayOf(PropTypes.string).isRequired,
    linkedinProfile: PropTypes.string,
  }).isRequired,
  onOpenModal: PropTypes.func.isRequired,
  onClickCompany: PropTypes.func.isRequired,
};

export default CompanyCard;
