import React, { useEffect, useState } from 'react';
import { fetchCompanies, deleteCompany } from '../../utils/api';
import CompanyForm from './CompanyForm';

const CompanyList = () => {
  const [companies, setCompanies] = useState([]);
  const [selectedCompanyId, setSelectedCompanyId] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const data = await fetchCompanies();
      setCompanies(data);
    };
    fetchData();
  }, []);

  const handleDelete = async (id) => {
    await deleteCompany(id);
    setCompanies(companies.filter((company) => company._id !== id));
  };

  const handleSave = () => {
    setSelectedCompanyId(null);
    const fetchData = async () => {
      const data = await fetchCompanies();
      setCompanies(data);
    };
    fetchData();
  };

  return (
    <div className="bg-gradient-to-b from-[#121212] to-black text-[#B3B3B3] min-h-screen p-6">
      <h2 className="text-3xl font-bold mb-6">Company List</h2>
      <CompanyForm companyId={selectedCompanyId} onSave={handleSave} />
      <table className="min-w-full bg-white rounded-lg shadow-lg">
        <thead>
          <tr>
            <th className="py-2 px-4 border-b">Name</th>
            <th className="py-2 px-4 border-b">Location</th>
            <th className="py-2 px-4 border-b">LinkedIn Profile</th>
            <th className="py-2 px-4 border-b">Emails</th>
            <th className="py-2 px-4 border-b">Phone Numbers</th>
            <th className="py-2 px-4 border-b">Comments</th>
            <th className="py-2 px-4 border-b">Communication Periodicity</th>
            <th className="py-2 px-4 border-b">Actions</th>
          </tr>
        </thead>
        <tbody>
          {companies.map((company) => (
            <tr key={company._id}>
              <td className="py-2 px-4 border-b">{company.name}</td>
              <td className="py-2 px-4 border-b">{company.location}</td>
              <td className="py-2 px-4 border-b">{company.linkedinProfile}</td>
              <td className="py-2 px-4 border-b">{company.emails.join(', ')}</td>
              <td className="py-2 px-4 border-b">{company.phoneNumbers.join(', ')}</td>
              <td className="py-2 px-4 border-b">{company.comments}</td>
              <td className="py-2 px-4 border-b">{company.communicationPeriodicity}</td>
              <td className="py-2 px-4 border-b">
                <button
                  className="bg-blue-500 text-white px-4 py-2 rounded mr-2"
                  onClick={() => setSelectedCompanyId(company._id)}
                >
                  Edit
                </button>
                <button
                  className="bg-red-500 text-white px-4 py-2 rounded"
                  onClick={() => handleDelete(company._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CompanyList;
