import React, { useState, useEffect } from 'react';
import { fetchCompanies } from '../../utils/api';
import CompanyGrid from './CompanyGrid';
import CommunicationModal from './CommunicationModal';

const Dashboard = () => {
  const [companies, setCompanies] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedCompanyId, setSelectedCompanyId] = useState(null);
  const [companyDetails, setCompanyDetails] = useState({});

  useEffect(() => {
    const getCompanies = async () => {
      const data = await fetchCompanies();
      setCompanies(data);
    };
    getCompanies();
  }, []);

  const handleOpenModal = (companyId) => {
    setSelectedCompanyId(companyId);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedCompanyId(null);
  };

  const handleCompanyDetails = (companyId) => {
    const company = companies.find((c) => c._id === companyId);
    setCompanyDetails(company); // Set the selected company details (name, city, phone, email, etc.)
  };

  return (
    <div className="dashboard">
      <h1 className="text-2xl font-bold mb-4">User Dashboard</h1>
      <div className="company-details mb-4">
        <p><strong>Company:</strong> {companyDetails?.name}</p>
        <p><strong>City:</strong> {companyDetails?.location}</p>
        <p><strong>Phone:</strong> {companyDetails?.phoneNumbers?.join(', ')}</p>
        <p><strong>Email:</strong> {companyDetails?.emails?.join(', ')}</p>
      </div>
      <CompanyGrid companies={companies} onOpenModal={handleOpenModal} onClickCompany={handleCompanyDetails} />
      {isModalOpen && (
        <CommunicationModal
          isOpen={isModalOpen}
          onRequestClose={handleCloseModal}
          companyId={selectedCompanyId}
          existingCommunications={[]}
        />
      )}
    </div>
  );
};

export default Dashboard;
