import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { addCompany, updateCompany, fetchCompanyById } from '../../utils/api';

const CompanyForm = ({ companyId, onSave }) => {
  const [company, setCompany] = useState({
    name: '',
    location: '',
    linkedinProfile: '',
    emails: [],
    phoneNumbers: [],
    comments: '',
    communicationPeriodicity: '2 weeks',
  });

  useEffect(() => {
    if (companyId) {
      fetchCompanyById(companyId).then((data) => setCompany(data));
    }
  }, [companyId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCompany((prevCompany) => ({ ...prevCompany, [name]: value }));
  };

  const handleArrayChange = (e) => {
    const { name, value } = e.target;
    setCompany((prevCompany) => ({
      ...prevCompany,
      [name]: value.split(',').map((item) => item.trim()),
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (companyId) {
      await updateCompany(companyId, company);
    } else {
      await addCompany(company);
    }
    onSave();
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={company.name}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Location:</label>
        <input
          type="text"
          name="location"
          value={company.location}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>LinkedIn Profile:</label>
        <input
          type="text"
          name="linkedinProfile"
          value={company.linkedinProfile}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Emails:</label>
        <input
          type="text"
          name="emails"
          value={company.emails.join(', ')}
          onChange={handleArrayChange}
        />
      </div>
      <div>
        <label>Phone Numbers:</label>
        <input
          type="text"
          name="phoneNumbers"
          value={company.phoneNumbers.join(', ')}
          onChange={handleArrayChange}
        />
      </div>
      <div>
        <label>Comments:</label>
        <textarea
          name="comments"
          value={company.comments}
          onChange={handleChange}
        ></textarea>
      </div>
      <div>
        <label>Communication Periodicity:</label>
        <input
          type="text"
          name="communicationPeriodicity"
          value={company.communicationPeriodicity}
          onChange={handleChange}
        />
      </div>
      <button type="submit">Save</button>
    </form>
  );
};

CompanyForm.propTypes = {
  companyId: PropTypes.string,
  onSave: PropTypes.func.isRequired,
};

export default CompanyForm;
