import React from 'react';
import PropTypes from 'prop-types';

// CompanyCard.jsx
const CompanyCard = ({ company, onOpenModal }) => {
  return (
    <div className="company-card">
      <h3>{company.name}</h3>
      <p>Email: {company.email}</p> {/* Ensure email is shown */}
      <p>Location: {company.location}</p>
      <p>Phone: {company.phone}</p>
      <button onClick={() => onOpenModal(company._id)}>Log Communication</button>
    </div>
  );
};

CompanyCard.propTypes = {
  company: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    location: PropTypes.string.isRequired,
    emails: PropTypes.arrayOf(PropTypes.string).isRequired,
    phoneNumbers: PropTypes.arrayOf(PropTypes.string).isRequired,
  }).isRequired,
  onOpenModal: PropTypes.func.isRequired,
};

export default CompanyCard;
